package week1.day1;

public class PositiveOrNegativeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 35;
		if (i > 0) {
			System.out.println("The Number is Positive");
		} else if (i < 0) {
			System.out.println("The Number is Negative");
		}
	}
}
